import sysfrom text_to_book import TextToBookdef main():    print('Input book text (press Ctrl+D to stop) '          'to split it into pages and generate /give command:')    text_to_book = TextToBook()    pages = text_to_book.split_on_pages(sys.stdin.read())    print(text_to_book.generate_give_from_pages(pages, '', ''))    print('Book pages:')    for i, page in enumerate(pages):        print('* Page', i + 1)        print(page)if __name__ == '__main__':    main()